import React from 'react'
import {BootstrapTable, TableHeaderColumn, InsertButton } from 'react-bootstrap-table';
import '../../../node_modules/react-bootstrap-table/dist/react-bootstrap-table-all.min.css';
import $ from 'jquery';

handleInsertButtonClick = (onClick) => {
  // Custom your onClick event here,
  // it's not necessary to implement this function if you have no any process before onClick
  console.log('This is my custom function for InserButton click event');
  onClick();
}
createCustomInsertButton = (onClick) => {
  return (
    <InsertButton
      btnText='CustomInsertText'
      btnContextual='btn-warning'
      className='my-custom-class'
      btnGlyphicon='glyphicon-edit'
      onClick={ () => this.handleInsertButtonClick(onClick) }/>
  );
}
//...
const options = {
  insertBtn: this.createCustomInsertButton
};

class Speakers extends React.Component {
    constructor(props) {
        super(props);
        this.getData();
        this.state = {
            data: this.data
        }
        this.CustomInsertButton();
    }

    getData() {
        return $.ajax({
            method: "GET",
            url: "/Speaker/all",    
            type: "json",
            contentType: "application/json",
            success: (response) => {
                this.setState({
                    data: response
                });
            }
        }).catch(function (xhr, status, error) {
            // TODO: Some form of error handling.
            console.log(error);
            this.setState({
                data: []
            });
        });
    }


    addRowHook(row) {
        const data = this.state.data || [];

        // Clear our the id field since the backend will auto generate one for us
        row.id = null;
        $.ajax({
            method: "POST",
            url: "/Speaker/addSpeaker",
            data: JSON.stringify(row),
            type: "json",
            contentType: "application/json",
            success: (response) => {
                data.push(response);
                this.setState({
                    data: data
                });
            }
        }).catch(function (xhr, status, error) {
            // TODO: Some form of error handling.
            console.log(error);
        });
    }

    deleteRowHook(rowIdsToDelete) {
        $.ajax({
            method: "POST",
            url: "/Speaker/deleteSpeaker",
            data: JSON.stringify(rowIdsToDelete),
            type: "json",
            contentType: "application/json",
            success: (response) => {
                // TODO: Implement some undo function
            }
        }).catch(function (xhr, status, error) {
            // TODO: Some form of error handling.
            console.log(error);
        });
    }

    beforeSaveHook(row, cellName, cellValue, done) {
        let mockRow = {...row};
        mockRow[cellName] = cellValue;
        $.ajax({
            method: "POST",
            url: "/Speaker/updateSpeaker",
            data: JSON.stringify(mockRow),
            type: "json",
            contentType: "application/json",
            success: (response) => {
                // TODO: Implement some undo function
                done(true);
            }
        }).catch(function (xhr, status, error) {
            // TODO: Some form of error handling.
            console.log(error);
            done(false);
        });

        return 1;
    }

    cannotBeEmptyValidator(value, row) {
        const response = {isValid: true, notification: {type: 'success', msg: '', title: ''}};
        if (!value) {
            response.isValid = false;
            response.notification.type = 'error';
            response.notification.msg = 'Value must be inserted';
            response.notification.title = 'Requested Value';
        }
        return response;
    }

    tableProps = {
        onAddRow: this.addRowHook.bind(this),
        afterDeleteRow: this.deleteRowHook.bind(this)
    };

    cellEditProps = {
        mode: 'click',
        blurToSave: true,
        beforeSaveCell: this.beforeSaveHook.bind(this)
    };


    
    render() {
        return (
            <>
                <div className="card">
                    <div className="card-header">
                        <h3>Speakers</h3>
                    </div>
                </div>
                <div>
                    <BootstrapTable striped hover condensed
                                    data={this.state.data}
                        // pagination
                                    exportCSV
                                    version='4'
                                    insertRow
                                    deleteRow
                                    selectRow={{mode: 'checkbox'}}
                                    options={this.tableProps}
                                    options={options}
                                    cellEdit={this.cellEditProps}>
                        <TableHeaderColumn hidden hiddenOninsert autoValue isKey dataField='id'>id</TableHeaderColumn>
                        <TableHeaderColumn dataField='speakerName' editable={{validator: this.cannotBeEmptyValidator}}>Speaker
                            Name</TableHeaderColumn>
                        <TableHeaderColumn dataField='email'>Email</TableHeaderColumn>
                        <TableHeaderColumn dataField='everydayNumber'>Everyday Number</TableHeaderColumn>
                        <TableHeaderColumn dataField='dayOfNumber'>Day Of Number</TableHeaderColumn>
                    </BootstrapTable>
                </div>
            </>
        )
    }
}

export default Speakers
